import { createSignal, Setter, Switch, Match } from "solid-js"
import { clones } from "~/lib/ls"

// Holds all valid SVG patterns numbers depending on the number of clones on the canvas
const [patternsAll, setPatternsAll] = createSignal<number[]>(validSVGIndex(clones().length))

// Holds the current state (pattern number)
const [patternNo, setPatternNo] = createSignal<number>(patternsAll()[0])

// Holds a DOM reference to the current svg pattern element
const [patternEl, setPatternEl] = createSignal<SVGPatternElement>(null)

export { patternsAll, setPatternsAll, patternNo, setPatternNo, patternEl, setPatternEl }

// Define the valid SVG:s depending on the number of clones on the canvas
export function validSVGIndex(clones: number): number[] {
    console.log(clones)
    const components = []
    if (clones == 2) {
        components.push(0, 10)
    }
    if (clones < 4) {
        components.push(4, 1, 2, 11, 3)
    } else if (clones < 5) {
        components.push(5, 6, 7)
    } else {
        components.push(5, 7, 8, 9)
    }
    console.log(components.flat())
    return components.flat()
}

type SelectSVGPatternProps = {
    setPatternEl: Setter<SVGPatternElement>
    isAbsolute?: boolean
}

export const SelectSVGPattern = (props: SelectSVGPatternProps) => {
    console.log(patternNo())
    return (
        <Switch>
            <Match when={patternNo() == 0}>{<Zebra ref={props.setPatternEl} isAbsolute={props.isAbsolute} />}</Match>
            <Match when={patternNo() == 1}>
                {<Herringbone ref={props.setPatternEl} isAbsolute={props.isAbsolute} />}
            </Match>
            <Match when={patternNo() == 2}>{<Blobs ref={props.setPatternEl} isAbsolute={props.isAbsolute} />}</Match>
            <Match when={patternNo() == 3}>{<Squares ref={props.setPatternEl} isAbsolute={props.isAbsolute} />}</Match>
            <Match when={patternNo() == 4}>{<Leafs ref={props.setPatternEl} isAbsolute={props.isAbsolute} />}</Match>
            <Match when={patternNo() == 5}>{<Flower ref={props.setPatternEl} isAbsolute={props.isAbsolute} />}</Match>
            <Match when={patternNo() == 6}>{<S6ties ref={props.setPatternEl} isAbsolute={props.isAbsolute} />}</Match>
            <Match when={patternNo() == 7}>
                {<ManyLeafs ref={props.setPatternEl} isAbsolute={props.isAbsolute} />}
            </Match>
            <Match when={patternNo() == 8}>{<Tribal ref={props.setPatternEl} isAbsolute={props.isAbsolute} />}</Match>
            <Match when={patternNo() == 9}>{<Circles ref={props.setPatternEl} isAbsolute={props.isAbsolute} />}</Match>
            <Match when={patternNo() == 10}>{<Net ref={props.setPatternEl} isAbsolute={props.isAbsolute} />}</Match>
            <Match when={patternNo() == 11}>{<Dots ref={props.setPatternEl} isAbsolute={props.isAbsolute} />}</Match>
        </Switch>
    )
}

